
public class Main {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = 10;
		
		a++;
		System.out.printf("a ++ ==> %d \n", a);

		a--;
		System.out.printf("a -- ==> %d \n", a);
		
		a += 5;
		System.out.printf("a += 5 ==> %d \n", a);
		
		a -= 5;
		System.out.printf("a -= 5 ==> %d \n", a);
		
		a *= 5;
		System.out.printf("a *= 5 ==> %d \n", a);
		
		a /= 5;
		System.out.printf("a /= 5 ==> %d \n", a);
		
		a %= 5;
		System.out.printf("a %%= 5 ==> %d \n", a);
		
		
	}

}
